import React, {useState, useContext, createContext, Children, FormEvent } from 'react'
import {User, UserRequest } from '../models/UserModel'

// interface talar om hur något ser ut
interface IUserProviderProps {
    children: any
}

interface IUserContext {
    user: User,
    setUser: React.Dispatch<React.SetStateAction<User>>
    users: User[],
    create: (e: React.FormEvent<UserRequest>) => void,
    get: (id:number) => void,
    getAll: () => void,
    update: (id:number, e: React.FormEvent<User>) => void,
    remove: (id: number) => void
}

export const  UserContext = createContext<IUserContext | null >(null)
export const useUserContext = () => {
    return useContext(UserContext)
}

const UserProvider = ({children}: IUserProviderProps) => {

    const [user, setUser] = useState<User>({})
    const [users, setUsers] = useState<User[]>([])
  
    const create = async (e: React.FormEvent<UserRequest>) => {}
    const get = async (id : number) => {}
    const getAll = async () => {}
    const update = async (id: number, e: React.FormEvent<User>) => {}
    const remove = async () => {}

  
  
    return (
    <div>
        {/* value säger vad som ska vara tillgängligt för children */}
        <UserContext.Provider value={{user, setUser, users, create, get, getAll, update, remove}}>
            {children}
        </UserContext.Provider>
    </div>
  )
}

export default UserProvider
