import {createContext, useContext, useState} from 'react'
import { ProductItem } from '../models/ProductModel'

interface ProductProviderType{
    children: any
}

export interface ProductContextType {
    product: ProductItem,
    products: ProductItem[],
    featuredProducts: ProductItem[],
    getProduct: (articleNumber: string) => void,
    getProducts: (take?: number) => void,
}

export const ProductContext = createContext<ProductContextType | null>(null)

export const useProductContext = () => {
    return useContext(ProductContext)
}

export const ProductProvider: React.FC<ProductProviderType> = ({children}) => {
    const EMPTY_PRODUCT: ProductItem = {
        articleNumber:'',
        name:'',
        category:'',
        price: 0,
        imageName:''
    }

    const [product, setProduct] = useState<ProductItem>(EMPTY_PRODUCT)
    const [products, setProducts] = useState<ProductItem[]>([])
    const [featuredProducts, setFeaturedProducts] = useState<ProductItem[]>([])

    const baseUrl: string = 'https://win22-webapi.azurewebsites.net/api/products'


    const getProduct = async (articleNumber: string) => {
        const res = await fetch(url + `/${articleNumber}`)
        setProduct(await res.json())
    }

    const getProducts = async (take = 0) => {
        let url = baseUrl

        if (take !==0) {
            url = baseUrl + `?take=${take}`
        } 
        const res = await fetch(url)
        setProducts(await res.json())
    }

    return <ProductContext.Provider value={{product, products, featuredProducts, getProduct, getProducts}}>{children}</ProductContext.Provider>
}
