import {createContext, useContext, useState} from 'react'
import { ProductItem } from '../models/ProductModel'

interface ProductProviderType{
    children: any
}

export interface ProductContextType {
    product: ProductItem
}

export const ProductContext = createContext<ProductContextType | null>(null)

export const useProductContext = () => {
    return useContext(ProductContext)
}

export const ProductProvider: React.FC<ProductProviderType> = ({children}) => {
    const empty_values: ProductItem = {
        articleNumber:'',
        name:'',
        category:'',
        
    }

    const [product, setProduct] = useState<ProductItem>({})

    const url = 'https://win22-webapi.azurewebsites.net/api/products'
    const [products, setProducts] = useState()
    const [featuredProducts, setFeaturedProducts] = useState()


    const getProduct = async (articleNumber) => {
        const res = await fetch(url + `/${articleNumber}`)
        setProduct(await res.json())
    }

    const getProducts = async () => {
        const res = await fetch(url)
        setProducts(await res.json())
    }

    const getFeaturedProducts = async (take = 0) => {
        const res = await fetch(url + `?take=${take}`)
        setProducts(await res.json())
    }

    return <ProductContext.Provider value={{product, products, featuredProducts, getProduct, getProducts, getFeaturedProducts}}>{children}</ProductContext.Provider>
}
