import React, { useEffect } from 'react'
import {IUserContext, UserContext} from '../contexts/UserContext'
import { User } from '../models/UserModel'

const UserList = () => {

    const {users, getAll, remove} = React.useContext(UserContext) as IUserContext

    useEffect(() => {
        getAll()

    }, [getAll])

  return (
    <>

    <h3 className='display-6 mb-4'>List of Users</h3>
    {
        users.map((user: User) => (<div key={user.id} className='mb-3'>{user.firstName}{user.lastName}<button onClick={(e) => remove(user.id)}></button></div>))
    }

    </>
  )
}

export default UserList
